mapper.insert:
INSERT INTO REFRESH_TOKEN (
  MEMBER_NO,
  REFRESH_TOKEN,
  EXPIRATION_DATE
) VALUES (
  #{memberNo},
  #{refreshToken},
  #{expirationDate}
)

mapper.update:
UPDATE REFRESH_TOKEN SET
  REFRESH_TOKEN = #{refreshToken},
  EXPIRATION_DATE = #{expirationDate}
WHERE MEMBER_NO = #{memberNo}
