mapper.select:
SELECT COUNT(*) FROM FAVORITES
WHERE MEMBER_NO = #{memberNo}
AND CONTENT_ID = #{contentId}

mapper.delete:
DELETE FROM FAVORITES
WHERE MEMBER_NO = #{memberNo}
AND CONTENT_ID = #{contentId}

DELETE FROM FESTIVAL_LIKE
WHERE ROWID IN (
  SELECT ROWID FROM FESTIVAL_LIKE
  WHERE CONTENT_ID = #{contentId}
  AND ROWNUM = 1
)
