mapper.select:
SELECT * FROM ( SELECT ROWNUM rn, t.* FROM ( SELECT b.BOARD_NO as boardNo, b.BOARD_CODE as boardTypeNo, bt.BOARD_NAME as boardTypeName, b.MEMBER_NO as memberNo, m.NICKNAME as memberNickname, m.PROFILE_IMAGE as member_profile_image, b.BOARD_TITLE as boardTitle, b.BOARD_CONTENT as boardContent, b.READ_COUNT as boardViewCount, b.BOARD_CREAT_DATE as boardCreateDate, b.BOARD_UPDATE_DATE as boardUpdateDate, b.BOARD_DEL_FL as boardDeleteYn, (SELECT COUNT(*) FROM "COMMENT" c WHERE c.BOARD_NO = b.BOARD_NO AND c.COMMENT_DEL_FL = 'N') as boardCommentCount, (SELECT COUNT(*) FROM "LIKE" l WHERE l.BOARD_NO = b.BOARD_NO) as boardLikeCount FROM BOARD b LEFT JOIN MEMBER m ON b.MEMBER_NO = m.MEMBER_NO JOIN BOARD_TYPE bt ON b.BOARD_CODE = bt.BOARD_CODE WHERE b.BOARD_DEL_FL = 'N'
AND b.BOARD_CODE = #{boardTypeNo}
AND b.BOARD_TITLE LIKE '%' || #{searchKeyword} || '%'
AND (b.BOARD_TITLE LIKE '%' || #{searchKeyword} || '%' OR b.BOARD_CONTENT LIKE '%' || #{searchKeyword} || '%')
AND m.NICKNAME LIKE '%' || #{searchKeyword} || '%'
ORDER BY b.BOARD_CREAT_DATE DESC ) t WHERE ROWNUM <= #{offset} + #{limit} ) WHERE rn > #{offset}

mapper.update:
UPDATE BOARD 
SET READ_COUNT = READ_COUNT + 1 
WHERE BOARD_NO = #{boardNo}

UPDATE BOARD 
SET BOARD_DEL_FL = 'Y' 
WHERE BOARD_NO = #{boardNo}

UPDATE BOARD 
SET BOARD_DEL_FL = 'N' 
WHERE BOARD_NO = #{boardNo}

mapper.select:
SELECT COUNT(*) 
FROM "LIKE" 
WHERE BOARD_NO = #{boardNo} AND MEMBER_NO = #{memberNo}
